UPDATE PlantillaComunicado SET pcoMensaje = 'Mensaje<br />  <p>${nombreCcf}</p><br /> <p>${logoDeLaCcf}</p><br /> <p>${responsableCcf}</p> <br /> <p>${nombreCompleto}</p><br /> ' WHERE pcoEtiqueta IN ('COM_GEN_CER_AFI','COM_GEN_CER_HIS_AFI','COM_GEN_CER_APO','COM_GEN_CER_PYS_EMP','COM_GEN_CER_PYS');
