INSERT RequisitoCajaClasificacion (rtsTextoAyuda,rtsEstado,rtsClasificacion,rtsTipoTransaccion,rtsRequisito,rtsCajaCompensacion) VALUES ('Si el empleador presenta trabajadores para afiliar, se solicita este documento y se revisa:-Tipo y número de documento de identidad de los trabajadores-Nombres y apellidos de los trabajadores-Salario de cada uno de los trabajadores-Se indique que los salarios de los trabajadores son causados en el Departamento-Firma de aval del Contador y/o el Representante legal-Debe coincidir la información de esta nómina con la registrada en el formulario de solicitud de afiliación','OPCIONAL','ENTIDAD_SIN_ANIMO_DE_LUCRO','AFILIACION_EMPLEADORES_WEB_REINTEGRO',(SELECT reqId FROM requisito WHERE reqDescripcion = 'Información de nómina de los trabajadores a afiliar'),(SELECT ccfId FROM cajacompensacion WHERE ccfNombre = 'CONFAMILIARES'));
