UPDATE PlantillaComunicado SET pcoCuerpo  =  CONCAT(pcoCuerpo , '<br /> <a href="${enlaceDeEnrolamiento}">Clic aquí </a>'), pcoMensaje = '<p>Se&ntilde;ores <br />${razonSocial/Nombre}<br />Las validaciones preliminares han sido exitosas. Para continuar con el proceso de afiliaci&oacute;n por favor seguir el siguiente v&iacute;nculo:<br /><br /> <a href="${enlaceDeEnrolamiento}">Clic aquí </a><br /><br />Cordialmente,<br />${nombreCcf}</p>'	WHERE pcoEtiqueta = 'NTF_ENRL_AFL_EMP_WEB';
