INSERT RequisitoCajaClasificacion (rtsEstado,rtsRequisito,rtsClasificacion,rtsTipoTransaccion,rtsCajaCompensacion,rtsTextoAyuda,rtsTipoRequisito) VALUES ('OBLIGATORIO',(SELECT reqId FROM Requisito WHERE reqDescripcion = 'Documento evidencia de sociedad conyugal o unión marital de hecho'),'CONYUGE_HOGAR ','MEJORAMIENTO_VIVIENDA_RURAL_PRESENCIAL',1,'Copia del registro civil de matrimonio o declaración juramentada para acreditar la unión libre (formato Ministerio de Trabajo). Se revisa:<br />-No debe tener tachones o enmendaduras<br />-El tipo y número de documento de identidad del Jefe de hogar y su cónyuge deben coincidir con los registrados en los demás documentos soporte<br />-Firmado por el solicitante (en el caso de declaración juramentada)','ESTANDAR');
