INSERT RequisitoCajaClasificacion (rtsEstado,rtsRequisito,rtsClasificacion,rtsTipoTransaccion,rtsCajaCompensacion,rtsTextoAyuda,rtsTipoRequisito) VALUES ('OBLIGATORIO',(SELECT reqId FROM Requisito WHERE reqDescripcion = 'Copia del documento de identidad'),'CONYUGE_HOGAR ','MEJORAMIENTO_VIVIENDA_RURAL_WEB',1,'Tarjeta de identidad para menores de 18 años de edad, cédula de ciudadanía o cédula de extranjería para mayores de 18 años de edad. Se revisa:<br />-No debe tener tachones o enmendaduras<br />-El tipo y número de documento de identidad deben coincidir con el registrado en el formulario de postulación<br />-Los nombres y apellidos deben corresponder a los registrados en el formulario de postulación<br />-La fecha de nacimiento debe corresponder a la registrada en el formulario de postulación','ESTANDAR');
