--comment:Creacion de las tablas NotificacionPersonal y NotificacionPersonalDocumento
CREATE TABLE NotificacionPersonal(
	ntpId BIGINT IDENTITY(1,1) NOT NULL,
	ntpActividad VARCHAR (41) NOT NULL,
	ntpPersona BIGINT NOT NULL,
	ntpTipoSolicitante VARCHAR (13) NOT NULL,
	ntpComentario VARCHAR (250) NULL,
CONSTRAINT PK_NotificacionPersonal_ntpId PRIMARY KEY (ntpId)
);
ALTER TABLE NotificacionPersonal ADD CONSTRAINT FK_NotificacionPersonal_ntpPersona FOREIGN KEY(ntpPersona) REFERENCES Persona (perId);

CREATE TABLE NotificacionPersonalDocumento(
	ntdId BIGINT IDENTITY(1,1) NOT NULL,
	ntdDocumentoSoporte BIGINT NOT NULL,
CONSTRAINT PK_NotificacionPersonalDocumento_ntdId PRIMARY KEY (ntdId)
);
ALTER TABLE NotificacionPersonalDocumento ADD CONSTRAINT FK_NotificacionPersonalDocumento_ntdDocumentoSoporte FOREIGN KEY(ntdDocumentoSoporte) REFERENCES DocumentoSoporte (dosId);

