named-native-query.query:
declare @perid bigint select @perid = perId from Persona where perTipoIdentificacion = :tipoIdentificacion and perNumeroIdentificacion = :numeroIdentificacion
declare @ValidacionLiquidacion table (sllTipoLiquidacion varchar(33), sllTipoLiquidacionEspecifica varchar(32), rvlNumeroRadicado varchar(23), sllFechaInicio date, cpeRazonSocial varchar (250) , cumple int, cpeTipoIdentificacion varchar (32), cpeNumeroIdentificacion varchar(16), cpeEmp bigint,cpeTrb bigint, rvlEstadoDerechoLiquidacion varchar(50)) insert @ValidacionLiquidacion SELECT sllTipoLiquidacion, sllTipoLiquidacionEspecifica, rvlNumeroRadicado, sllFechaInicio, cpe.cpeRazonSocial, 1 AS cumple, cpe.cpeTipoIdentificacion, cpe.cpeNumeroIdentificacion,cpe.cpeId cpeEmp,cpt.cpeId cpeTrb, rvlEstadoDerechoLiquidacion FROM dbo.ResultadoValidacionLiquidacion WITH (NOLOCK) INNER JOIN dbo.SolicitudLiquidacion WITH (NOLOCK) ON sllNumeroRadicacion = rvlNumeroRadicado INNER JOIN dbo.CondicionEmpleador WITH (NOLOCK) ON rvlCondicionEmpleador = cemId AND rvlNumeroRadicado = cemNumeroRadicado INNER JOIN dbo.CondicionPersona cpe WITH (NOLOCK) ON cemCondicionPersona = cpe.cpeId AND rvlNumeroRadicado = cpe.cpeNumeroRadicado INNER JOIN dbo.CondicionTrabajador WITH (NOLOCK) ON rvlCondicionTrabajador = ctrId AND rvlNumeroRadicado = ctrNumeroRadicado INNER JOIN dbo.CondicionPersona cpt WITH (NOLOCK) ON ctrCondicionPersona = cpt.cpeId AND rvlNumeroRadicado = cpt.cpeNumeroRadicado WHERE rvlCondicionBeneficiario IS NOT NULL AND rvlEstadoDerechoLiquidacion IN ('ASIGNAR_DERECHO', 'DERECHO_ASIGNADO', 'DERECHO_DISPERSADO_EN_MEDIOS_DE_PAGO') AND (:tipoLiquidacion IS NULL OR sllTipoLiquidacion = :tipoLiquidacion) and sllTipoLiquidacion <> 'SUBSUDIO_DE_DEFUNCION' and cpt.cpePersona = @perid union SELECT sllTipoLiquidacion, sllTipoLiquidacionEspecifica, rvl.rvlNumeroRadicado, sllFechaInicio, cpe.cpeRazonSocial, 0 AS cumple, cpe.cpeTipoIdentificacion, cpe.cpeNumeroIdentificacion,cpe.cpeId cpeEmp,cpt.cpeId cpeTrb, rvlEstadoDerechoLiquidacion FROM dbo.ResultadoValidacionLiquidacion as rvl WITH (NOLOCK) INNER JOIN dbo.SolicitudLiquidacion WITH (NOLOCK) ON sllNumeroRadicacion = rvl.rvlNumeroRadicado INNER JOIN dbo.CondicionEmpleador WITH (NOLOCK) ON rvl.rvlCondicionEmpleador = cemId AND rvl.rvlNumeroRadicado = cemNumeroRadicado INNER JOIN dbo.CondicionPersona cpe WITH (NOLOCK) ON cemCondicionPersona = cpe.cpeId AND rvl.rvlNumeroRadicado = cpe.cpeNumeroRadicado INNER JOIN dbo.CondicionTrabajador WITH (NOLOCK) ON rvl.rvlCondicionTrabajador = ctrId AND rvl.rvlNumeroRadicado = ctrNumeroRadicado INNER JOIN dbo.CondicionPersona cpt WITH (NOLOCK) ON ctrCondicionPersona = cpt.cpeId AND rvl.rvlNumeroRadicado = cpt.cpeNumeroRadicado WHERE rvl.rvlEstadoDerechoLiquidacion NOT IN ('ASIGNAR_DERECHO', 'DERECHO_ASIGNADO', 'DERECHO_DISPERSADO_EN_MEDIOS_DE_PAGO') AND (:tipoLiquidacion IS NULL OR sllTipoLiquidacion = :tipoLiquidacion) and sllTipoLiquidacion <> 'SUBSUDIO_DE_DEFUNCION' and cpt.cpePersona = @perid union SELECT sllTipoLiquidacion, sllTipoLiquidacionEspecifica, rvlNumeroRadicado, sllFechaInicio, cpe.cpeRazonSocial, 0 AS cumple, cpe.cpeTipoIdentificacion, cpe.cpeNumeroIdentificacion,cpe.cpeId cpeEmp,cpt.cpeId cpeTrb, rvlEstadoDerechoLiquidacion FROM dbo.ResultadoValidacionLiquidacion WITH (NOLOCK) INNER JOIN dbo.SolicitudLiquidacion WITH (NOLOCK) ON sllNumeroRadicacion = rvlNumeroRadicado INNER JOIN dbo.CondicionEmpleador WITH (NOLOCK) ON rvlCondicionEmpleador = cemId AND rvlNumeroRadicado = cemNumeroRadicado INNER JOIN dbo.CondicionPersona cpe WITH (NOLOCK) ON cemCondicionPersona = cpe.cpeId AND rvlNumeroRadicado = cpe.cpeNumeroRadicado INNER JOIN dbo.CondicionTrabajador WITH (NOLOCK) ON rvlCondicionTrabajador = ctrId AND rvlNumeroRadicado = ctrNumeroRadicado INNER JOIN dbo.CondicionPersona cpt WITH (NOLOCK) ON ctrCondicionPersona = cpt.cpeId AND rvlNumeroRadicado = cpt.cpeNumeroRadicado WHERE rvlCondicionBeneficiario IS NULL AND rvlNumeroRadicado NOT IN (SELECT DISTINCT rvlNumeroRadicado FROM ResultadoValidacionLiquidacion WITH (NOLOCK) WHERE rvlCondicionBeneficiario IS NOT NULL) AND (:tipoLiquidacion IS NULL OR sllTipoLiquidacion = :tipoLiquidacion) and sllTipoLiquidacion <> 'SUBSUDIO_DE_DEFUNCION' and cpt.cpePersona = @perid;
WITH LiquidacionData AS ( SELECT rvlNumeroRadicado, SUM(rvlValorCuota) AS montoLiquidado, SUM(rvlValorDescuento) AS montoDescontado, SUM(rvlValorCuotaAjustada) AS montoParaPago, cpe.cpeId cpeEmp,cpt.cpeId cpeTrb , rvlEstadoDerechoLiquidacion FROM dbo.ResultadoValidacionLiquidacion WITH (NOLOCK) INNER JOIN dbo.CondicionTrabajador WITH (NOLOCK) ON rvlCondicionTrabajador = ctrId AND rvlNumeroRadicado = ctrNumeroRadicado INNER JOIN dbo.CondicionPersona cpt WITH (NOLOCK) ON ctrCondicionPersona = cpt.cpeId AND rvlNumeroRadicado = cpt.cpeNumeroRadicado INNER JOIN dbo.CondicionEmpleador WITH (NOLOCK) ON rvlCondicionEmpleador = cemId AND rvlNumeroRadicado = cemNumeroRadicado INNER JOIN dbo.CondicionPersona cpe WITH (NOLOCK) ON cemCondicionPersona = cpe.cpeId AND rvlNumeroRadicado = cpe.cpeNumeroRadicado WHERE cpt.cpePersona = @perid GROUP BY rvlNumeroRadicado, cpe.cpeId,cpt.cpeId,rvlEstadoDerechoLiquidacion) ,RankedRows as ( SELECT ROW_NUMBER() OVER(PARTITION BY rvlNumeroRadicado, cpeTipoIdentificacion, cpeNumeroIdentificacion ORDER BY cumple DESC) AS [Row Number], rvlNumeroRadicado, sllFechaInicio, sllTipoLiquidacion, sllTipoLiquidacionEspecifica, cpeRazonSocial, cumple, cpeTipoIdentificacion, cpeNumeroIdentificacion, cpeEmp,cpeTrb,rvlEstadoDerechoLiquidacion FROM @ValidacionLiquidacion ) SELECT a.rvlNumeroRadicado, a.sllFechaInicio, a.sllTipoLiquidacion, a.sllTipoLiquidacionEspecifica, a.cpeRazonSocial, a.cumple, c.montoLiquidado, c.montoDescontado, c.montoParaPago, a.cpeTipoIdentificacion, a.cpeNumeroIdentificacion FROM RankedRows a LEFT JOIN ( SELECT rvlNumeroRadicado, CONVERT (VARCHAR(10), MAX(plsPeriodo)) AS periodo FROM dbo.ResultadoValidacionLiquidacion WITH (NOLOCK) INNER JOIN dbo.PeriodoLiquidacionSubsidio WITH (NOLOCK) ON plsNumeroRadicado = rvlNumeroRadicado INNER JOIN dbo.CondicionTrabajador WITH (NOLOCK) ON rvlCondicionTrabajador = ctrId AND rvlNumeroRadicado = ctrNumeroRadicado INNER JOIN dbo.CondicionPersona cpt WITH (NOLOCK) ON ctrCondicionPersona = cpt.cpeId AND rvlNumeroRadicado = cpt.cpeNumeroRadicado INNER JOIN dbo.CondicionEmpleador WITH (NOLOCK) ON rvlCondicionEmpleador = cemId AND rvlNumeroRadicado = cemNumeroRadicado INNER JOIN dbo.CondicionPersona cpemp WITH (NOLOCK) ON cemCondicionPersona = cpemp.cpeId AND rvlNumeroRadicado = cpemp.cpeNumeroRadicado WHERE plsTipoPeriodo = 'REGULAR' and cpt.cpePersona = @perid GROUP BY rvlNumeroRadicado ) AS b ON a.rvlNumeroRadicado = b.rvlNumeroRadicado LEFT JOIN LiquidacionData AS c ON a.rvlNumeroRadicado = c.rvlNumeroRadicado AND a.cpeTrb = c.cpeTrb AND a.cpeEmp = c.cpeEmp AND a.rvlEstadoDerechoLiquidacion = c.rvlEstadoDerechoLiquidacion WHERE [Row Number] = 1 AND (:numPeriodo = 0 OR periodo in (:periodo)) AND (:fechaInicio IS NULL OR CAST(sllFechaInicio AS DATE) >= CAST(:fechaInicio AS DATE)) AND (:fechaFin IS NULL OR CAST(sllFechaInicio AS DATE) <= CAST(:fechaFin AS DATE)) AND (:numeroRadicado IS NULL OR a.rvlNumeroRadicado = :numeroRadicado) ORDER BY sllFechaInicio desc
